// src/components/ModelViewer.jsx
import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, useTexture } from '@react-three/drei';

// Model component for loading the GLTF model and applying multiple textures
const Model = () => {
  // Load the model
  const { scene } = useGLTF('/models/scene.gltf');

  // Load textures
  const hexTexture = useTexture('/textures/hex.001_baseColor.png');
  const gradientTexture = useTexture('/textures/lpb_gradient_baseColor.png');

  // Traverse the model and apply textures to specific meshes/materials
  scene.traverse((child) => {
    if (child.isMesh) {
      if (child.name === 'HexMesh') {
        // Replace 'HexMesh' with the name of the specific mesh to apply hexTexture
        child.material.map = hexTexture;
      } else if (child.name === 'GradientMesh') {
        // Replace 'GradientMesh' with the name of the specific mesh to apply gradientTexture
        child.material.map = gradientTexture;
      }
      child.material.needsUpdate = true;
    }
  });

  // Set scale or position if needed
  scene.scale.set(1, 1, 1);
  return <primitive object={scene} />;
};

const ModelViewer = () => {
  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <Canvas camera={{ position: [0, 2, 5], fov: 60 }}>
        {/* Lighting for the model */}
        <ambientLight intensity={0.3} />
        <directionalLight position={[2, 5, 5]} intensity={1} />

        {/* Load the Model */}
        <Model />

        {/* Controls to orbit around the model */}
        <OrbitControls />
      </Canvas>
    </div>
  );
};

export default ModelViewer;
