// Lost.jsx
import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import backgroundImg from '../images/allbg.png';

const Lost = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Extract finalScore from location state; default to 0 if not provided
  const { finalScore } = location.state || { finalScore: 0 };

  useEffect(() => {
    console.log('Final Score:', finalScore); // Verify finalScore in the console
  }, [finalScore]);

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-r from-red-400 to-yellow-500"
      style={{ backgroundImage: `url(${backgroundImg})`, backgroundSize: 'cover' }}
    >
      <div className="p-8 bg-white rounded-lg shadow-lg text-center">
        <h1 className="text-4xl font-bold text-red-600 mb-4">
          ‚ùå Game Over!
        </h1>
        <p className="text-xl text-gray-700 mb-6">
          You Lost! Try again!
        </p>

        <div className="flex gap-4 justify-center">
          {/* Retry Button */}
          <button
            onClick={() => navigate('/Home')}
            aria-label="Retry the game"
            className="px-6 py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
          >
            Home
          </button>

          {/* Start New Game Button */}
          <button
            onClick={() => navigate('/Game')}
            aria-label="Return to Home to Start a New Game"
            className="px-6 py-3 bg-teal-600 text-white rounded-md hover:bg-teal-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-teal-400"
          >
            Start New Game
          </button>
        </div>
      </div>
    </div>
  );
};

export default Lost;
