// Won.jsx
import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import backgroundImg from '../images/allbg.png'; // Adjust the path based on your project structure

const Won = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Extract finalScore from location state
  const { finalScore } = location.state || { finalScore: 0 };

  useEffect(() => {
    console.log('Won.jsx - Final Score:', finalScore);
  }, [finalScore]);

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-cover bg-center bg-no-repeat"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      {/* Overlay for better text readability */}
      <div className="absolute inset-0 bg-black opacity-10"></div>

      <div className="relative z-10 p-8 bg-black bg-opacity-30 rounded-lg shadow-lg text-center max-w-lg">
        <h1 className="text-4xl font-bold text-green-600 mb-4">
          ðŸŽ‰ Congratulations!
        </h1>
        <p className="text-xl text-white mb-6">
          You've successfully completed the game with a total score of{' '}
          <span className="font-bold">{finalScore}</span>.
        </p>

        {/* Buttons */}
        <div className="flex justify-center gap-4">
          {/* Play Again Button */}
          <button
            onClick={() => navigate('/game')} // Navigate back to Game
            aria-label="Play the game again"
            className="px-6 py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-blue-400"
          >
            Play Again
          </button>

          {/* Home Button */}
          <button
            onClick={() => navigate('/home')} // Navigate to Home
            aria-label="Return to home page"
            className="px-6 py-3 bg-gray-600 text-white rounded-md hover:bg-gray-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-gray-400"
          >
            Home
          </button>
        </div>
      </div>
    </div>
  );
};

export default Won;
