import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImg from '../images/allbg.png'; // Verify that this path is correct
import '../App.css'; // Ensure this import is included to apply CSS animations

const Home = () => {
  const navigate = useNavigate();

  const startGame = () => {
    console.log('Navigating to /game');
    navigate('/game');
  };

  const goToAbout = () => {
    console.log('Navigating to /about');
    navigate('/about');
  };

  return (
    <div
      className="relative w-full min-h-screen bg-cover bg-center bg-no-repeat flex flex-col items-center justify-center p-8"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      <div className="absolute inset-0 bg-black opacity-10"></div>

      <div className="relative z-2 max-w-4xl w-full bg-black bg-opacity-80 dark:bg-gray-800 dark:bg-opacity-45 rounded-lg shadow-2xl p-8 animate-scale-in">
      <div className="mb-8 flex items-center justify-center animate-fade-in">
  <h1 className="text-center text-5xl font-extrabold text-teal-700 dark:text-teal-300 transition duration-300 transform hover:scale-105 ease-in-out">
    MI Game
  </h1>
</div>


        <div className="flex flex-col sm:flex-row justify-center gap-6 animate-fade-in delay-200">
          <button
            onClick={startGame}
            className="px-8 py-4 bg-teal-300 text-black rounded-full hover:bg-teal-700 transition-all duration-500 ease-in-out shadow-lg transform hover:scale-110 focus:outline-none focus:ring-4 focus:ring-teal-400 animate-pulse"
          >
            Start Playing
          </button>

          <button
            onClick={goToAbout}
            className="px-8 py-4 bg-gray-600 text-white rounded-full hover:bg-gray-700 transition-all duration-500 ease-in-out shadow-lg transform hover:scale-110 focus:outline-none focus:ring-4 focus:ring-gray-400 animate-pulse"
          >
            How to Play
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
