// ThreeBackground.jsx
import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const Model = () => {
  const { scene } = useGLTF('/models/scene.gltf'); // Ensure the model path is correct
  scene.scale.set(1, 1, 1); // Adjust scale as necessary
  return <primitive object={scene} />;
};

const ThreeBackground = () => {
  return (
    <Canvas camera={{ position: [0, 2, 5], fov: 10 }} className="absolute inset-0 w-full h-full z-0">
      <ambientLight intensity={0.5} color="red" />
      <pointLight position={[-5, 5, 5]} color="yellow" intensity={1.5} />
      <pointLight position={[5, -5, 5]} color="blue" intensity={1.5} />
      <pointLight position={[0, 5, -5]} color="green" intensity={1.5} />

      <Model />

      <OrbitControls />
    </Canvas>
  );
};

export default ThreeBackground;
