// About.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import backgroundImg from '../images/allbg.png'; // Adjust the path based on your project structure

const About = () => {
  const navigate = useNavigate();

  const startPlaying = () => {
    navigate('/Game'); // Redirects to the Game page
  };

  return (
    <div
      className="relative w-full min-h-screen bg-cover bg-center bg-no-repeat flex flex-col items-center justify-center p-8"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      {/* Overlay for better text readability */}
      <div className="absolute inset-0 bg-black opacity-20"></div>

      {/* Content Container */}
      <div className="relative z-10 max-w-4xl w-full bg-white bg-opacity-30 dark:bg-gray-800 dark:bg-opacity-70 rounded-lg shadow-2xl p-8 overflow-auto">
        {/* Header Section */}
        <div className="mb-6">
          <h1 className="text-center text-4xl font-extrabold text-teal-300 dark:text-teal-300">
            How to Play
          </h1>
        </div>

        {/* Game Rules */}
        <div className="mb-8">
          <ul className="list-disc list-inside text-gray-300 dark:text-gray-300 space-y-4">
            <li>
              <strong>Objective:</strong> Drag and drop numerical values into bins to maximize your total score.
            </li>
            <li>
              <strong>Bin Capacity:</strong> Each bin can hold values with a combined total of up to <span className="font-bold">40</span>.
            </li>
            <li>
              <strong>Score Goal:</strong> Aim for a total score greater than <span className="font-bold">150</span> to win the game.
            </li>
            <li>
              <strong>Time Limit:</strong> You have <span className="font-bold">5 minutes</span> to place all values into the bins.
            </li>
            <li>
              <strong>Selection:</strong> Click on a value to select it before dragging it into a bin. Multiple selections are allowed.
            </li>
            <li>
              <strong>Optimize:</strong> Use the "Optimize Bins" feature to automatically distribute remaining values efficiently.
            </li>
            <li>
              <strong>Final Score:</strong> The game concludes when all values are placed or time runs out. Your final score is the sum of all values in the bins.
            </li>
          </ul>
        </div>

        {/* Start Playing Button */}
        <div className="flex justify-center">
          <button
            onClick={startPlaying}
            className="px-8 py-4 bg-teal-600 text-white rounded-full hover:bg-teal-500 transition-colors duration-300 shadow-lg transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-teal-400 focus:ring-opacity-50"
          >
            Start Playing
          </button>
        </div>
      </div>
    </div>
  );
};

export default About;
