import React, { useState, useEffect } from 'react';
import { useDrag, useDrop, DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useNavigate } from 'react-router-dom';
import './Game.css';

// Import images
import backgroundImg from '../images/allbg.png';
import bin1Img from '../images/b1.png';
import bin2Img from '../images/b2.png';
import bin3Img from '../images/b3.png';
import bin4Img from '../images/b4.png';

const ItemTypes = {
  VALUE: 'value',
};

// Utility function to calculate bin sum
const calculateBinSum = (bin) => bin.reduce((sum, item) => sum + item.value, 0);

// Draggable Value Component
function DraggableValue({ id, value, isSelected, toggleSelect, isPlaced, highlight }) {
  const [{ isDragging }, drag] = useDrag(
    () => ({
      type: ItemTypes.VALUE,
      item: { id, value, selectedIds: isSelected ? [id] : [id] },
      canDrag: !isPlaced,
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }),
    [isSelected, isPlaced, id, value]
  );

  return (
    <div
      ref={drag}
      onClick={() => toggleSelect(id)}
      className={`p-3 bg-white rounded-md text-center cursor-pointer transition-transform duration-200 
        ${isPlaced ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-100 transform hover:scale-105'} 
        ${isSelected ? 'ring-2 ring-teal-500' : ''} 
        ${highlight ? 'border-4 border-yellow-500' : ''} 
        shadow-md animate-fade-in`}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      role="button"
      aria-pressed={isSelected}
      tabIndex={0}
      onKeyPress={(e) => {
        if (e.key === 'Enter') toggleSelect(id);
      }}
    >
      {value}
    </div>
  );
}

// Droppable Bin Component
function Bin({ bin, index, onDrop, isGameComplete, values, highlightBin }) {
  const [{ isOver, canDrop }, drop] = useDrop(
    () => ({
      accept: ItemTypes.VALUE,
      drop: (item) => onDrop(item.selectedIds, index),
      canDrop: (item) => {
        const currentSum = calculateBinSum(bin);
        const newSum = item.selectedIds.reduce((sum, id) => {
          const draggedValue = values.find((val) => val.id === id);
          return sum + (draggedValue ? draggedValue.value : 0);
        }, 0);
        return currentSum + newSum <= 40 && !isGameComplete;
      },
      collect: (monitor) => ({
        isOver: monitor.isOver(),
        canDrop: monitor.canDrop(),
      }),
    }),
    [bin, isGameComplete, values]
  );

  const isActive = isOver && canDrop;

  // Map bin index to corresponding image
  const binImages = [bin1Img, bin2Img, bin3Img, bin4Img];
  const binImage = binImages[index % binImages.length];

  return (
    <div
      ref={drop}
      className={`relative p-4 border border-gray-300 rounded-md min-w-[150px] flex-1 flex flex-col items-center transition-transform duration-200 
        ${isActive ? 'shadow-lg transform scale-105 border-teal-500' : 'bg-white'}
        ${highlightBin === index ? 'border-yellow-500 shadow-xl' : ''} animate-fade-in`}
    >
      <img src={binImage} alt={`Bin ${index + 1}`} className="w-30 h- mb-5 mx-auto" />
      <div className="flex flex-wrap gap-2 min-h-[40px] justify-center">
        {bin.map((item) => (
          <div key={item.id} className="p-2 bg-teal-300 rounded-md text-center shadow-sm">
            {item.value}
          </div>
        ))}
      </div>
      <p className="mt-2 text-gray-700 font-medium text-center">Sum: {calculateBinSum(bin)}</p>
      <p className="mt-1 text-gray-500 text-sm text-center">Remaining Capacity: {40 - calculateBinSum(bin)}</p>
      {isActive && <div className="absolute inset-0 bg-teal-100 bg-opacity-50 rounded-md pointer-events-none"></div>}
    </div>
  );
}

function Game() {
  const [values, setValues] = useState(
    Array.from({ length: 20 }, (_, idx) => ({
      id: idx,
      value: Math.floor(Math.random() * 20) + 1,
    }))
  );

  const [bins, setBins] = useState([[], [], [], []]);
  const [isGameComplete, setIsGameComplete] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [selectedIds, setSelectedIds] = useState([]);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes in seconds
  const [hintsUsed, setHintsUsed] = useState(0);
  const [hint, setHint] = useState(null);
  const navigate = useNavigate();

  // Toggle selection of a value
  const toggleSelect = (id) => {
    setSelectedIds((prev) => (prev.includes(id) ? prev.filter((i) => i !== id) : [...prev, id]));
  };

  // Reset Game Function
  const resetGame = () => {
    setValues(
      Array.from({ length: 20 }, (_, idx) => ({
        id: idx,
        value: Math.floor(Math.random() * 20) + 1,
      }))
    );
    setBins([[], [], [], []]);
    setIsGameComplete(false);
    setFinalScore(0);
    setSelectedIds([]);
    setTimeLeft(300);
    setHintsUsed(0);
    setHint(null);
  };

  // Hint Button
  const handleHint = () => {
    if (hintsUsed >= 3 || isGameComplete) return;
    setHint('Try to balance the bins evenly.');
    setHintsUsed((prev) => prev + 1);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="game min-h-screen bg-cover bg-center bg-no-repeat p-8 flex flex-col items-center relative animate-scale-in"
           style={{ backgroundImage: `url(${backgroundImg})` }}>
        <div className="absolute inset-0 bg-gradient-to-b from-teal-800 to-teal-400 opacity-60"></div>

        {/* Controls */}
        <div className="controls flex w-full justify-between mb-6 animate-fade-in delay-200">
          <button
            onClick={resetGame}
            className="px-4 py-2 bg-gradient-to-r from-teal-500 to-teal-300 text-white rounded-full hover:bg-teal-600 transition-all duration-500 ease-in-out shadow-lg transform hover:scale-110 focus:outline-none focus:ring-4 focus:ring-teal-400 animate-pulse"
          >
            Restart
          </button>
          <button
            onClick={() => navigate('/Home')}
            className="px-4 py-2 bg-gradient-to-r from-red-500 to-red-300 text-white rounded-full hover:bg-red-600 transition-all duration-500 ease-in-out shadow-lg transform hover:scale-110 focus:outline-none focus:ring-4 focus:ring-red-400 animate-pulse"
          >
            Quit Game
          </button>
        </div>

        {/* Game Title */}
        <div className="p-2 mb-3 w-full max-w-2xl text-center animate-fade-in">
          <h1 className="inline-block bg-gray-800 bg-opacity-70 text-white text-4xl font-extrabold mb-2 px-4 py-5 rounded-md transform transition duration-300 hover:scale-105 ease-in-out">
            Bin Picking Game
          </h1>
        </div>

        {/* Timer and Hint Counter */}
        <div className="flex items-center justify-between w-full max-w-4xl mb-4 animate-fade-in">
          <div className="timer">
            <h2 className="inline-block bg-gray-800 bg-opacity-70 text-white text-2xl font-extrabold px-4 py-2 rounded-md">
              Time Left: {Math.floor(timeLeft / 60)}:{('0' + (timeLeft % 60)).slice(-2)}
            </h2>
          </div>
          <div className="hints">
            <h2 className="inline-block bg-gray-800 bg-opacity-70 text-white text-2xl font-extrabold px-4 py-2 rounded-md">
              Hints Used: {hintsUsed} / 3
            </h2>
          </div>
        </div>

        {/* Values Pool */}
        <div className="values p-6 mb-6 border border-gray-400 rounded-md bg-gray-800 bg-opacity-75 w-full max-w-4xl shadow-lg animate-fade-in">
          <h2 className="mb-4 text-white text-2xl font-bold">Values</h2>
          <div className="flex flex-wrap gap-3">
            {values.map((val) => (
              <DraggableValue
                key={val.id}
                id={val.id}
                value={val.value}
                isPlaced={false}
                isSelected={selectedIds.includes(val.id)}
                toggleSelect={toggleSelect}
              />
            ))}
          </div>
        </div>

        {/* Bins */}
        <div className="bins flex flex-wrap justify-center gap-6 mb-6 w-full max-w-4xl animate-fade-in">
          {bins.map((bin, index) => (
            <Bin
              key={index}
              bin={bin}
              index={index}
              onDrop={() => {}}
              isGameComplete={isGameComplete}
              values={values}
            />
          ))}
        </div>

        {/* Hint Button */}
        <div className="flex gap-4 mb-6">
          <button
            onClick={handleHint}
            className={`px-6 py-3 bg-yellow-500 text-white rounded-md hover:bg-yellow-600 transition-all duration-500 ease-in-out shadow-md transform hover:scale-110 focus:outline-none focus:ring-4 focus:ring-yellow-400 ${
              hintsUsed >= 3 || isGameComplete ? 'opacity-50 cursor-not-allowed' : 'animate-pulse'
            }`}
            disabled={hintsUsed >= 3 || isGameComplete}
          >
            Hint
          </button>
        </div>

        {/* Final Score Display */}
        {isGameComplete && (
          <div className="mt-6 p-6 bg-yellow-300 rounded-md shadow-lg text-center animate-fade-in">
            <h2 className="text-3xl font-bold">Final Score: {finalScore}</h2>
            <p className="text-green-700 mt-4 animate-bounce">
              {finalScore >= 150 ? '🎉 Congratulations! You\'ve won the game.' : '❌ You did not achieve the required score. Try again!'}
            </p>
          </div>
        )}
      </div>
    </DndProvider>
  );
}

export default Game;
