import React, { useState, useEffect } from 'react';
import { useDrag, useDrop, DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useNavigate } from 'react-router-dom';
import './Game.css';
import backgroundImg from '../images/allbg.png';
import bin1Img from '../images/b1.png';
import bin2Img from '../images/b2.png';
import bin3Img from '../images/b3.png';
import bin4Img from '../images/b4.png';

const ItemTypes = {
  VALUE: 'value',
};

// Utility function to calculate bin sum
const calculateBinSum = (bin) => bin.reduce((sum, item) => sum + item.value, 0);

// Draggable Value Component
function DraggableValue({ id, value, isSelected, toggleSelect, isPlaced, highlight }) {
  const [{ isDragging }, drag] = useDrag(() => ({
      type: ItemTypes.VALUE,
      item: { id, value, selectedIds: isSelected ? [id] : [id] },
      canDrag: !isPlaced,
      collect: (monitor) => ({ isDragging: monitor.isDragging() }),
    }),
    [isSelected, isPlaced, id, value]
  );

  return (
    <div
      ref={drag}
      onClick={() => toggleSelect(id)}
      className={`p-3 bg-white rounded-md text-center cursor-pointer transition-transform duration-200 
        ${isPlaced ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-100 transform hover:scale-105'} 
        ${isSelected ? 'ring-2 ring-teal-500' : ''} 
        ${highlight ? 'border-4 border-yellow-500' : ''} 
        shadow-md`}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      role="button"
      aria-pressed={isSelected}
      tabIndex={0}
      onKeyPress={(e) => {
        if (e.key === 'Enter') toggleSelect(id);
      }}
    >
      {value}
    </div>
  );
}

// Droppable Bin Component
function Bin({ bin, index, onDrop, isGameComplete, values, highlightBin }) {
  const [{ isOver, canDrop }, drop] = useDrop(() => ({
      accept: ItemTypes.VALUE,
      drop: (item) => onDrop(item.selectedIds, index),
      canDrop: (item) => {
        const currentSum = calculateBinSum(bin);
        const newSum = item.selectedIds.reduce((sum, id) => {
          const draggedValue = values.find((val) => val.id === id);
          return sum + (draggedValue ? draggedValue.value : 0);
        }, 0);
        return currentSum + newSum <= 40 && !isGameComplete;
      },
      collect: (monitor) => ({
        isOver: monitor.isOver(),
        canDrop: monitor.canDrop(),
      }),
    }),
    [bin, isGameComplete, values]
  );

  const isActive = isOver && canDrop;
  const binImages = [bin1Img, bin2Img, bin3Img, bin4Img];
  const binImage = binImages[index % binImages.length];

  return (
    <div
      ref={drop}
      className={`relative p-4 border border-gray-300 rounded-md min-w-[150px] flex-1 flex flex-col items-center transition-transform duration-200 
        ${isActive ? 'shadow-lg transform scale-105 border-teal-500' : 'bg-white'}
        ${highlightBin === index ? 'border-yellow-500 shadow-xl' : ''}`}
    >
      <img src={binImage} alt={`Bin ${index + 1}`} className="w-30 h- mb-5 mx-auto" />
      <div className="flex flex-wrap gap-2 min-h-[40px] justify-center">
        {bin.map((item) => (
          <div key={item.id} className="p-2 bg-teal-300 rounded-md text-center shadow-sm">
            {item.value}
          </div>
        ))}
      </div>
      <p className="mt-2 text-gray-700 font-medium text-center">
        Sum: {calculateBinSum(bin)}
      </p>
      <p className="mt-1 text-gray-500 text-sm text-center">
        Remaining Capacity: {40 - calculateBinSum(bin)}
      </p>
      {isActive && (
        <div className="absolute inset-0 bg-teal-100 bg-opacity-50 rounded-md pointer-events-none"></div>
      )}
    </div>
  );
}

function Game() {
  const [values, setValues] = useState(
    Array.from({ length: 20 }, (_, idx) => ({
      id: idx,
      value: Math.floor(Math.random() * 20) + 1,
    }))
  );
  const [bins, setBins] = useState([[], [], [], []]);
  const [isGameComplete, setIsGameComplete] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [selectedIds, setSelectedIds] = useState([]);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes in seconds
  const [hintsUsed, setHintsUsed] = useState(0); // Track hints used
  const [showTitle, setShowTitle] = useState(true); // Show title initially
  const navigate = useNavigate();

  // Toggle selection of a value
  const toggleSelect = (id) => {
    setSelectedIds((prev) =>
      prev.includes(id) ? prev.filter((i) => i !== id) : [...prev, id]
    );
  };

  // Start Game and Hide Title Overlay
  const startGame = () => {
    setShowTitle(false);
  };

  // Timer Countdown
  useEffect(() => {
    if (!showTitle && timeLeft > 0 && !isGameComplete) {
      const timerId = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timerId);
    }
  }, [timeLeft, showTitle, isGameComplete]);

  // Reset Game
  const resetGame = () => {
    setValues(
      Array.from({ length: 20 }, (_, idx) => ({
        id: idx,
        value: Math.floor(Math.random() * 20) + 1,
      }))
    );
    setBins([[], [], [], []]);
    setIsGameComplete(false);
    setFinalScore(0);
    setSelectedIds([]);
    setTimeLeft(300);
    setHintsUsed(0);
    setShowTitle(true); // Show title again on reset
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div
        className="game min-h-screen bg-cover bg-center bg-no-repeat p-8 flex flex-col items-center relative"
        style={{ backgroundImage: `url(${backgroundImg})` }}
      >
        <div className="absolute inset-0 bg-gradient-to-b from-teal-800 to-teal-400 opacity-60"></div>

        {/* Title Overlay */}
        {showTitle && (
          <div
            className="absolute inset-0 flex flex-col items-center justify-center bg-black bg-opacity-50 z-10 text-white"
            onClick={startGame}
          >
            <h1 className="text-5xl font-extrabold mb-4">Bin Picking Game</h1>
            <button className="px-6 py-3 bg-teal-500 rounded-full text-lg hover:bg-teal-600 transition duration-300">
              Start Game
            </button>
          </div>
        )}

        {/* Game Controls */}
        {!showTitle && (
          <>
            <div className="controls flex w-full justify-between mb-6">
              <button
                onClick={resetGame}
                className="px-4 py-2 bg-gradient-to-r from-teal-500 to-teal-300 text-white rounded-full hover:bg-teal-600 transition duration-300 shadow-md focus:outline-none focus:ring-4 focus:ring-teal-400"
              >
                Restart
              </button>
              <button
                onClick={() => navigate('/Home')}
                className="px-4 py-2 bg-gradient-to-r from-red-500 to-red-300 text-white rounded-full hover:bg-red-600 transition duration-300 shadow-md focus:outline-none focus:ring-4 focus:ring-red-400"
              >
                Quit Game
              </button>
            </div>

            {/* Remaining Game Elements (Bins, Timer, Values) */}
            {/* ... (Continue with remaining game elements) */}
          </>
        )}
      </div>
    </DndProvider>
  );
}

export default Game;
