// Game.jsx
import React, { useState, useEffect } from 'react';
import ThreeBackground from './ThreeBackground';

const Game = () => {
  const [step, setStep] = useState(0);
  const [message, setMessage] = useState("Press 'Start Game' to begin the magic!");
  const [randomEvenNumber, setRandomEvenNumber] = useState(10);
  const [answer, setAnswer] = useState(5);
  const [timeLeft, setTimeLeft] = useState(300);

  const initializeGame = () => {
    const evenNumber = Math.floor(Math.random() * 10) * 2 + 2;
    setRandomEvenNumber(evenNumber);
    setAnswer(evenNumber / 2);
  };

  const startGame = () => {
    setStep(1);
    setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
    setTimeLeft(300);
    initializeGame();
  };

  useEffect(() => {
    if (timeLeft > 0 && step > 0 && step <= 5) {
      const timerId = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timerId);
    } else if (timeLeft === 0) {
      setMessage("Time's up! Please restart the game.");
      setStep(0);
    }
  }, [timeLeft, step]);

  const nextStep = () => {
    switch (step) {
      case 1:
        setMessage("Step 1: Multiply your number by 2.");
        break;
      case 2:
        setMessage(`Step 2: Add ${randomEvenNumber} to the result.`);
        break;
      case 3:
        setMessage("Step 3: Divide the result by 2.");
        break;
      case 4:
        setMessage("Step 4: Now, subtract your original number from the result.");
        break;
      case 5:
        setMessage(`The answer is... ${answer}! ðŸŽ‰ Isnâ€™t it magic?`);
        break;
      default:
        setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
        setStep(1);
        return;
    }
    setStep(step + 1);
  };

  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  const resetGame = () => {
    setStep(0);
    setMessage("Press 'Start Game' to begin the magic!");
    setTimeLeft(300);
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/images/gamebg.png'})`, // Correct path
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        width: '100vw',
        height: '100vh',
      }}
    >
      {/* Three.js Stereo Background */}
      <ThreeBackground />

      {/* Main Game Content */}
      <div className="flex flex-col items-center justify-center min-h-screen p-6 text-white relative z-10">
        <div className="bg-black bg-opacity-60 rounded-lg p-16 shadow-2xl max-w-2xl text-center transform scale-110">
          <h1 className="text-5xl font-bold mb-8 text-yellow-300">Magic Number Guess</h1>
          <p className="text-2xl font-medium mb-8">{message}</p>

          {step > 0 && step <= 5 && (
            <p className="text-2xl font-semibold text-red-500 mb-8">Time Left: {formatTime(timeLeft)}</p>
          )}

          {step === 0 ? (
            <button
              onClick={startGame}
              className="px-8 py-4 bg-green-500 text-2xl text-white font-semibold rounded-lg hover:bg-green-600 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-green-400 shadow-lg"
            >
              Start Game
            </button>
          ) : (
            <div className={`flex ${step > 1 ? 'justify-between w-full' : 'justify-center'} max-w-lg mt-8`}>
              {step > 1 && (
                <button
                  onClick={prevStep}
                  className="px-6 py-3 bg-gray-500 text-2xl text-white font-semibold rounded-lg hover:bg-gray-600 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-gray-400 shadow-lg"
                >
                  Back
                </button>
              )}
              {step <= 5 ? (
                <button
                  onClick={nextStep}
                  className={`px-8 py-4 bg-yellow-400 text-2xl text-gray-800 font-semibold rounded-lg hover:bg-yellow-500 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-yellow-300 shadow-lg ${step > 1 ? '' : 'mx-auto'}`}
                >
                  Next
                </button>
              ) : (
                <button
                  onClick={resetGame}
                  className="px-8 py-4 bg-purple-600 text-2xl text-white font-semibold rounded-lg hover:bg-purple-700 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-purple-400 shadow-lg"
                >
                  Restart
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Game;
