// Game.jsx
import React, { useState, useEffect } from 'react';
import { useDrag, useDrop, DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useNavigate } from 'react-router-dom';
import './Game.css';
import branchAndBound from './branchAndBound'; // Import the branch and bound algorithm

// Import images
import backgroundImg from '../images/allbg.png';
import bin1Img from '../images/b1.png';
import bin2Img from '../images/b2.png';
import bin3Img from '../images/b3.png';
import bin4Img from '../images/b4.png';

const ItemTypes = {
  VALUE: 'value',
};

// Utility function to calculate bin sum
const calculateBinSum = (bin) => bin.reduce((sum, item) => sum + item.value, 0);

function DraggableValue({ id, value, isSelected, toggleSelect, isPlaced, highlight }) {
  const [{ isDragging }, drag] = useDrag(
    () => ({
      type: ItemTypes.VALUE,
      item: { id, value, selectedIds: isSelected ? [id] : [id] },
      canDrag: !isPlaced,
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }),
    [isSelected, isPlaced, id, value]
  );

  return (
    <div
      ref={drag}
      onClick={() => toggleSelect(id)}
      className={`p-3 bg-white rounded-md text-center cursor-pointer transition-transform duration-200 
        ${isPlaced ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-100 transform hover:scale-105'} 
        ${isSelected ? 'ring-2 ring-blue-500' : ''} 
        ${highlight ? 'border-4 border-yellow-500' : ''} 
        shadow-md`}
      style={{ opacity: isDragging ? 0.5 : 1 }}
      role="button"
      aria-pressed={isSelected}
      tabIndex={0}
      onKeyPress={(e) => {
        if (e.key === 'Enter') toggleSelect(id);
      }}
    >
      {value}
    </div>
  );
}

// Droppable Bin Component
function Bin({ bin, index, onDrop, isGameComplete, values, highlightBin }) {
  const [{ isOver, canDrop }, drop] = useDrop(
    () => ({
      accept: ItemTypes.VALUE,
      drop: (item) => onDrop(item.selectedIds, index),
      canDrop: (item) => {
        const currentSum = calculateBinSum(bin);
        const newSum = item.selectedIds.reduce((sum, id) => {
          const draggedValue = values.find((val) => val.id === id);
          return sum + (draggedValue ? draggedValue.value : 0);
        }, 0);
        return currentSum + newSum <= 40 && !isGameComplete;
      },
      collect: (monitor) => ({
        isOver: monitor.isOver(),
        canDrop: monitor.canDrop(),
      }),
    }),
    [bin, isGameComplete, values]
  );

  const isActive = isOver && canDrop;

  // Map bin index to corresponding image
  const binImages = [bin1Img, bin2Img, bin3Img, bin4Img];
  const binImage = binImages[index % binImages.length];

  return (
    <div
      ref={drop}
      className={`relative p-4 border border-gray-300 rounded-md min-w-[150px] flex-1 flex flex-col items-center transition-transform duration-200 
        ${isActive ? 'shadow-lg transform scale-105 border-green-500' : 'bg-white'}
        ${highlightBin === index ? 'border-yellow-500 shadow-xl' : ''}`}
    >
      <img src={binImage} alt={`Bin ${index + 1}`} className="w-30 h- mb-5 mx-auto" />

      <div className="flex flex-wrap gap-2 min-h-[40px] justify-center">
        {bin.map((item) => (
          <div key={item.id} className="p-2 bg-blue-300 rounded-md text-center shadow-sm">
            {item.value}
          </div>
        ))}
      </div>

      <p className="mt-2 text-gray-700 font-medium text-center">Sum: {calculateBinSum(bin)}</p>
      <p className="mt-1 text-gray-500 text-sm text-center">
        Remaining Capacity: {40 - calculateBinSum(bin)}
      </p>
      {isActive && (
        <div className="absolute inset-0 bg-green-100 bg-opacity-50 rounded-md pointer-events-none"></div>
      )}
    </div>
  );
}

function Game() {
  // Initialize values with unique IDs
  const [values, setValues] = useState(
    Array.from({ length: 20 }, (_, idx) => ({
      id: idx,
      value: Math.floor(Math.random() * 20) + 1,
    }))
  );

  const [bins, setBins] = useState([[], [], [], []]);
  const [isGameComplete, setIsGameComplete] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [selectedIds, setSelectedIds] = useState([]);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes in seconds
  const [hintsUsed, setHintsUsed] = useState(0); // Track hints used
  const [hint, setHint] = useState(null); // Current hint
  const [highlightedValueId, setHighlightedValueId] = useState(null); // Highlighted value
  const [highlightedBinIndex, setHighlightedBinIndex] = useState(null); // Highlighted bin
  const [optimalSolution, setOptimalSolution] = useState(null); // To store optimal solution
  const navigate = useNavigate();

  // Toggle selection of a value
  const toggleSelect = (id) => {
    setSelectedIds((prev) =>
      prev.includes(id) ? prev.filter((i) => i !== id) : [...prev, id]
    );
  };

  // Handle Drop Action for Multiple Values
  const handleDrop = (droppedIds, binIndex) => {
    if (isGameComplete) return;

    // Remove the values from the values array
    const newValues = values.filter((val) => !droppedIds.includes(val.id));

    // Extract the values to be added to the bin
    const movedValues = values.filter((val) => droppedIds.includes(val.id));

    // Add the values to the specified bin
    const newBins = [...bins];
    newBins[binIndex] = [...newBins[binIndex], ...movedValues];

    setValues(newValues);
    setBins(newBins);
    setSelectedIds([]); // Clear selections after drop

    // Clear any existing hints since the state has changed
    setHint(null);
    setHighlightedValueId(null);
    setHighlightedBinIndex(null);
  };

  // Calculate Final Score and Check Completion
  useEffect(() => {
    const totalSum = bins.reduce((acc, bin) => acc + calculateBinSum(bin), 0);
    setFinalScore(totalSum);

    // Check if win condition is met
    if (totalSum >= 150 || values.length === 0) {
      setIsGameComplete(true);
    }
  }, [bins, values]);

  // Timer Countdown
  useEffect(() => {
    if (isGameComplete) return;

    if (timeLeft === 0) {
      setIsGameComplete(true);
      return;
    }

    const timerId = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timerId);
  }, [timeLeft, isGameComplete]);

  // Display Completion Message and Redirect
  useEffect(() => {
    if (!isGameComplete) return;

    if (finalScore >= 150) {
      navigate('/Won', { state: { finalScore } });
    } else {
      navigate('/Lost', { state: { finalScore } });
    }
  }, [isGameComplete, finalScore, timeLeft, navigate]);

  // Reset Game Function
  const resetGame = () => {
    setValues(
      Array.from({ length: 20 }, (_, idx) => ({
        id: idx,
        value: Math.floor(Math.random() * 20) + 1,
      }))
    );
    setBins([[], [], [], []]);
    setIsGameComplete(false);
    setFinalScore(0);
    setSelectedIds([]);
    setTimeLeft(300); // Reset timer to 5 minutes
    setHintsUsed(0); // Reset hints used
    setHint(null); // Clear any hints
    setHighlightedValueId(null);
    setHighlightedBinIndex(null);
    setOptimalSolution(null); // Clear optimal solution
  };

  // Handle Hint Button Click
  const handleHint = () => {
    if (hintsUsed >= 3 || isGameComplete) return;
    setHint('Try to balance the bins evenly.');
    setHintsUsed((prev) => prev + 1);
  };

  // Function to compute optimal solution using Branch and Bound
  const findOptimalSolution = () => {
    const binCapacity = 40;
    const solution = branchAndBound(values, binCapacity);
    setOptimalSolution(solution); // Store the optimal solution to display
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="game min-h-screen bg-cover bg-center bg-no-repeat p-8 flex flex-col items-center"
           style={{ backgroundImage: `url(${backgroundImg})` }}>

        {/* Controls */}
        <div className="controls flex w-full justify-between mb-6">
          <button
            onClick={resetGame}
            className="px-4 py-2 bg-teal-600 text-white rounded-md hover:bg-teal-700 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-teal-400"
            aria-label="Start a new game"
          >
            Restart
          </button>
          <button
            onClick={() => navigate('/Home')}
            className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-red-400"
            aria-label="Quit the game and return home"
          >
            Quit Game
          </button>
        </div>

        {/* Optimal Solution Button */}
        <button
          onClick={findOptimalSolution}
          className="px-6 py-3 mb-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors duration-200"
          aria-label="Find optimal solution"
        >
          Find Optimal Solution
        </button>

        {/* Optimal Solution Display */}
        {optimalSolution && (
          <div className="mt-6 p-6 bg-yellow-200 rounded-md shadow-lg text-center">
            <h2 className="text-3xl font-bold">Optimal Solution:</h2>
            {optimalSolution.solution.map((bin, index) => (
              <div key={index} className="mt-2">
                <p className="text-xl">Bin {index + 1}: {bin.map(item => item.value).join(', ')}</p>
                <p className="text-sm">Sum: {calculateBinSum(bin)}</p>
              </div>
            ))}
            <p className="text-lg font-bold mt-4">Optimal Score: {optimalSolution.score}</p>
          </div>
        )}

        {/* Other game components (timer, bins, values pool, hints, etc.) remain unchanged */}

      </div>
    </DndProvider>
  );
}

export default Game;
