import React, { useState } from 'react';

const Game = () => {
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("Think of a number in your mind, and letâ€™s do some magic with it!");

  const nextStep = () => {
    switch (step) {
      case 1:
        setMessage("Step 1: Multiply your number by 2.");
        break;
      case 2:
        setMessage("Step 2: Add 10 to the result.");
        break;
      case 3:
        setMessage("Step 3: Divide the result by 2.");
        break;
      case 4:
        setMessage("Step 4: Now, subtract your original number from the result.");
        break;
      case 5:
        setMessage("The answer is... 5! ðŸŽ‰ Magic, isnâ€™t it?");
        break;
      default:
        setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
        setStep(0);
        break;
    }
    setStep(step + 1);
  };

  const resetGame = () => {
    setStep(1);
    setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Guess the Number in Your Mind</h1>
      <p>{message}</p>
      {step <= 5 ? (
        <button onClick={nextStep} style={buttonStyle}>Next</button>
      ) : (
        <button onClick={resetGame} style={buttonStyle}>Restart</button>
      )}
    </div>
  );
};

// Style for the button
const buttonStyle = {
  padding: '10px 20px',
  fontSize: '16px',
  cursor: 'pointer',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  marginTop: '20px',
};

export default Game;
