import React, { useState } from 'react';

const Game = () => {
  const [step, setStep] = useState(1);
  const [message, setMessage] = useState("Think of a number in your mind, and letâ€™s do some magic with it!");

  const nextStep = () => {
    switch (step) {
      case 1:
        setMessage("Step 1: Multiply your number by 2.");
        break;
      case 2:
        setMessage("Step 2: Add 10 to the result.");
        break;
      case 3:
        setMessage("Step 3: Divide the result by 2.");
        break;
      case 4:
        setMessage("Step 4: Now, subtract your original number from the result.");
        break;
      case 5:
        setMessage("The answer is... 5! ðŸŽ‰ Isnâ€™t it magic?");
        break;
      default:
        setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
        setStep(1);
        return;
    }
    setStep(step + 1);
  };

  const resetGame = () => {
    setStep(1);
    setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 p-6 text-white">
      <div className="bg-white bg-opacity-20 rounded-lg p-8 shadow-lg max-w-lg text-center">
        <h1 className="text-3xl font-bold mb-4 text-yellow-300">Magic Number Guess</h1>
        <p className="text-lg font-medium mb-8">{message}</p>
        {step <= 5 ? (
          <button
            onClick={nextStep}
            className="px-6 py-3 bg-yellow-400 text-gray-800 font-semibold rounded-lg hover:bg-yellow-500 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-yellow-300 shadow-md"
          >
            Next
          </button>
        ) : (
          <button
            onClick={resetGame}
            className="px-6 py-3 bg-purple-600 text-white font-semibold rounded-lg hover:bg-purple-700 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-purple-400 shadow-md"
          >
            Restart
          </button>
        )}
      </div>
    </div>
  );
};

export default Game;
