import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import { useNavigate } from 'react-router-dom';
import backgroundImg from '../images/allbg.png'; // Ensure this path is correct
import '../App.css'; // Import animations and styles

const Model = () => {
  const { scene } = useGLTF('/models/scene.gltf'); // Adjust the model path if necessary
  scene.scale.set(1, 1, 1); // Adjust scale as needed
  return <primitive object={scene} />;
};

const Home = () => {
  const navigate = useNavigate();

  const startGame = () => {
    console.log('Navigating to /game');
    navigate('/game');
  };

  const goToAbout = () => {
    console.log('Navigating to /about');
    navigate('/about');
  };

  return (
    <div
      className="relative w-full min-h-screen bg-cover bg-center bg-no-repeat flex flex-col items-center justify-center p-8"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      {/* 3D Model Canvas */}
      <Canvas
        className="absolute top-0 left-0 w-full h-full z-0"
        camera={{ position: [0, 2, 5], fov: 10 }}
      >
        <ambientLight intensity={0.5} color="red" />
        <pointLight position={[-5, 5, 5]} color="yellow" intensity={1.5} />
        <pointLight position={[5, -5, 5]} color="blue" intensity={1.5} />
        <pointLight position={[0, 5, -5]} color="green" intensity={1.5} />

        <Model />

        <OrbitControls />
      </Canvas>

      {/* Semi-transparent overlay */}
      <div className="bg-overlay absolute top-0 left-0 w-full h-full z-5"></div>

      {/* Main content box */}
      <div className="relative z-10 max-w-4xl w-full bg-white bg-opacity-10 rounded-lg shadow-2xl p-12 animate-scale-in">
        <div className="mb-10 flex items-center justify-center animate-fade-in">
          <h1 className="text-center text-6xl font-extrabold text-yellow-300 transition duration-300 transform hover:scale-105 ease-in-out animate-pulse">
            Mind Game
          </h1>
        </div>

        {/* Buttons for Start and About */}
        <div className="flex flex-col sm:flex-row justify-center gap-8 animate-fade-in delay-200">
          <button
            onClick={startGame}
            className="px-8 py-4 bg-yellow-400 text-gray-800 font-semibold text-xl rounded-lg hover:bg-yellow-500 transition-all duration-300 ease-in-out shadow-lg transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-yellow-300 animate-scale-in"
          >
            Start Playing
          </button>

          <button
            onClick={goToAbout}
            className="px-8 py-4 bg-purple-600 text-white font-semibold text-xl rounded-lg hover:bg-purple-700 transition-all duration-300 ease-in-out shadow-lg transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-purple-400 animate-scale-in"
          >
            How to Play
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
