import React, { useState, useEffect } from 'react';
import backgroundImg from '../images/allbg.png'; // Ensure this path is correct

const Game = () => {
  const [step, setStep] = useState(0); // Start at 0 for initial state
  const [message, setMessage] = useState("Press 'Start Game' to begin the magic!");
  const [randomEvenNumber, setRandomEvenNumber] = useState(10); // Random even number to add
  const [answer, setAnswer] = useState(5); // Half of the random even number
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes in seconds

  // Generate random even number and calculate answer on game start or reset
  const initializeGame = () => {
    const evenNumber = Math.floor(Math.random() * 10) * 2 + 2;
    setRandomEvenNumber(evenNumber);
    setAnswer(evenNumber / 2);
  };

  // Start Game
  const startGame = () => {
    setStep(1);
    setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
    setTimeLeft(300); // Reset timer to 5 minutes
    initializeGame();
  };

  // Timer effect
  useEffect(() => {
    if (timeLeft > 0 && step > 0 && step <= 5) { // Timer only runs when the game is active
      const timerId = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timerId);
    } else if (timeLeft === 0) {
      setMessage("Time's up! Please restart the game.");
      setStep(0); // End game when time runs out
    }
  }, [timeLeft, step]);

  // Advance steps in the game
  const nextStep = () => {
    switch (step) {
      case 1:
        setMessage("Step 1: Multiply your number by 2.");
        break;
      case 2:
        setMessage(`Step 2: Add ${randomEvenNumber} to the result.`);
        break;
      case 3:
        setMessage("Step 3: Divide the result by 2.");
        break;
      case 4:
        setMessage("Step 4: Now, subtract your original number from the result.");
        break;
      case 5:
        setMessage(`The answer is... ${answer}! ðŸŽ‰ Isnâ€™t it magic?`);
        break;
      default:
        setMessage("Think of a number in your mind, and letâ€™s do some magic with it!");
        setStep(1);
        return;
    }
    setStep(step + 1);
  };

  // Go back one step
  const prevStep = () => {
    if (step > 1) setStep(step - 1);
  };

  // Restart the game
  const resetGame = () => {
    setStep(0);
    setMessage("Press 'Start Game' to begin the magic!");
    setTimeLeft(300); // Reset timer to 5 minutes
  };

  // Format time as mm:ss
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <div
      className="flex flex-col items-center justify-center min-h-screen p-6 text-white"
      style={{
        backgroundImage: `url(${backgroundImg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <div className="bg-white bg-opacity-20 rounded-lg p-8 shadow-lg max-w-lg text-center">
        <h1 className="text-3xl font-bold mb-4 text-yellow-300">Magic Number Guess</h1>
        <p className="text-lg font-medium mb-4">{message}</p>

        {step > 0 && step <= 5 && (
          <p className="text-lg font-semibold text-red-500 mb-4">Time Left: {formatTime(timeLeft)}</p>
        )}

        {step === 0 ? (
          // Start Game Button
          <button
            onClick={startGame}
            className="px-6 py-3 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-green-400 shadow-md"
          >
            Start Game
          </button>
        ) : (
          // Game Buttons
          <div className={`flex ${step > 1 ? 'justify-between w-full' : 'justify-center'} max-w-xs mt-4`}>
            {step > 1 && (
              <button
                onClick={prevStep}
                className="px-4 py-2 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-gray-400 shadow-md"
              >
                Back
              </button>
            )}
            {step <= 5 ? (
              <button
                onClick={nextStep}
                className={`px-6 py-3 bg-yellow-400 text-gray-800 font-semibold rounded-lg hover:bg-yellow-500 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-yellow-300 shadow-md ${step > 1 ? '' : 'mx-auto'}`}
              >
                Next
              </button>
            ) : (
              <button
                onClick={resetGame}
                className="px-6 py-3 bg-purple-600 text-white font-semibold rounded-lg hover:bg-purple-700 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-purple-400 shadow-md"
              >
                Restart
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Game;
