// Game.jsx
import React, { useState, useEffect } from 'react';
import { Canvas, useLoader } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import * as THREE from 'three';
import backgroundImg from '../images/allbg.png'; // Adjust path as necessary

// Texture Imports
const hexTexturePath = '/textures/hex.001_baseColor.png';
const gradientTexturePath = '/textures/lpb_gradient_baseColor.png';

// Model component for loading the GLTF model and applying textures
const Model = () => {
  const { scene } = useGLTF('/models/scene.gltf'); // Load the model from public directory

  // Load textures
  const hexTexture = useLoader(THREE.TextureLoader, hexTexturePath);
  const gradientTexture = useLoader(THREE.TextureLoader, gradientTexturePath);

  // Apply textures to model materials based on mesh name
  scene.traverse((child) => {
    if (child.isMesh) {
      if (child.name.includes("Hex")) {
        child.material.map = hexTexture; // Apply hex texture to parts named "Hex"
      } else if (child.name.includes("Gradient")) {
        child.material.map = gradientTexture; // Apply gradient texture to parts named "Gradient"
      }
      child.material.needsUpdate = true; // Ensure materials update
    }
  });

  return <primitive object={scene} scale={3} />;
};

const Game = () => {
  const [step, setStep] = useState(0);
  const [message, setMessage] = useState("Press 'Start Game' to begin the magic!");
  const [timeLeft, setTimeLeft] = useState(300); // Timer in seconds

  // Game logic functions (initializeGame, startGame, etc.) go here
  const startGame = () => {
    setStep(1);
    setMessage("Think of a number in your mind, and let’s do some magic with it!");
    setTimeLeft(300); // Reset timer to 5 minutes
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
  };

  useEffect(() => {
    if (timeLeft > 0 && step > 0) { // Timer only runs when the game is active
      const timerId = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timerId);
    } else if (timeLeft === 0) {
      setMessage("Time's up! Please restart the game.");
      setStep(0); // End game when time runs out
    }
  }, [timeLeft, step]);

  return (
    <div
      className="flex flex-col items-center justify-center min-h-screen p-6 text-white"
      style={{
        backgroundImage: `url(${backgroundImg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <div className="bg-white bg-opacity-20 rounded-lg p-16 shadow-2xl max-w-2xl text-center transform scale-110">
        <h1 className="text-5xl font-bold mb-8 text-yellow-300">Magic Number Guess</h1>
        <p className="text-2xl font-medium mb-8">{message}</p>

        {/* Timer display and other UI elements */}
        {step > 0 && (
          <p className="text-2xl font-semibold text-red-500 mb-8">Time Left: {formatTime(timeLeft)}</p>
        )}

        {/* Start Button */}
        {step === 0 && (
          <button
            onClick={startGame}
            className="px-8 py-4 bg-green-500 text-2xl text-white font-semibold rounded-lg hover:bg-green-600 transition-colors duration-300 focus:outline-none focus:ring-4 focus:ring-green-400 shadow-lg"
          >
            Start Game
          </button>
        )}

        {/* Canvas for 3D Model Display */}
        <div className="mt-8 w-full h-96 canvas-container">
          <Canvas
            gl={{ toneMapping: THREE.ACESFilmicToneMapping }}
            camera={{ position: [0, 0, 10], fov: 50 }}
          >
            <ambientLight intensity={0.3} />
            <directionalLight position={[5, 5, 5]} intensity={1} />
            <pointLight position={[-5, -5, -5]} intensity={0.5} />
            <Model />
            <OrbitControls />
          </Canvas>
        </div>
      </div>
    </div>
  );
};

export default Game;
