// ModelViewer.jsx
import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const Model = () => {
  const { scene } = useGLTF('/models/scene.gltf'); // Load the GLTF model

  scene.scale.set(1, 1, 1); // Adjust scale if needed
  return <primitive object={scene} />;
};

const ModelViewer = () => {
  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <Canvas camera={{ position: [0, 2, 5], fov: 60 }}>
        {/* Adjust lighting */}
        <ambientLight intensity={1} />  {/* Increase ambient light */}
        <directionalLight position={[2, 5, 5]} intensity={1.5} /> {/* Increase directional light */}

        <Model />

        {/* Controls to orbit around the model */}
        <OrbitControls />
      </Canvas>
    </div>
  );
};

export default ModelViewer;
